import axios from 'axios';


// 封装get方法
export function post({ url, params }) {
   
    var abc =    axios.post(url, params).then(res => {
          return  res.data;
        }).catch(err => {
          return err.data;
        })
  }
  
  // 封装post方法
  export function get({ url, data }) {
    return new Promise((resolve, reject) => {
        axios.get(url, data).then(res => {
        resolve(res.data)
        }).catch(err => {
            reject(err.data)
        })
    })
  }
