import { get, post } from './axios.js' // 导入axios实例文件中方法

const server = {
  //带参数的 get 方法
  getById(param) {
    return get({
      url: '/role/getById',
      params: param
    })
  },
 
  //不带参数的 get 方法
  getAll() {
    return get({
      url: '/role/getAll',
    })
  },
  // post 方法
  save(param) {
    return post({
      url: '/api/api/userLogin/',
      data: param
    })
    axios.post('/api/api/userLogin/',{
        username:this.username,
        password:this.password
        }).then(res => {
        // this.data_res = res;
        this.is_show = res.data.status;
        console.log(res);
        }, res => {
            console.info('请求失败');
        })
  }
}

export default server
